// 方法:
// 使用自定义hash, 这样能O(n)对s的所有长度为l的子串进行hash
// 然后类似字母匹配的方式, 将间隔为l的子串视为字母, 进行匹配
class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        if(words.size() == 0 || s.size() == 0)
            return vector<int>();
        int W = words.size();
        int L = words[0].size();
        if(s.size() < L*W)
            return vector<int>();

        vector<int> acount(26, 0);
        unordered_map<string_view, int> wcount;
        using UMIT = unordered_map<string_view, int>::iterator;
        for(auto &i : words) {
            wcount[string_view(&i[0], L)]++;
            for(auto &c: i)
                acount[c-'a']++;
        }

        int num = 0;
        for(auto &i: acount) {
            if(i)
                num++;
        }

        vector<int> rlt;
        for(int i = 0; i < L*W; i++) {
            switch(acount[s[i]-'a']--) {
            case 0: num++; break;
            case 1: num--; break;
            }
        }
        if(num == 0) {
            unordered_map<string_view, int> wcopy(wcount);
            for(int i = 0; i < L*W; i+=L) {
                UMIT it = wcopy.find(string_view(&s[i], L));
                if(it == wcopy.end())
                    break;
                else if(--it->second == 0)
                    wcopy.erase(it);
            }
            if(wcopy.empty())
                rlt.emplace_back(0);
        }

        for(int i = 0; i < s.size()-L*W;) {
            switch(++acount[s[i]-'a']) {
            case 0: num--; break;
            case 1: num++; break;
            }
            switch(acount[s[i+L*W]-'a']--) {
            case 0: num++; break;
            case 1: num--; break;
            }
            i++;
            if(num == 0) {
                unordered_map<string_view, int> wcopy(wcount);
                for(int j = 0; j < L*W; j+=L) {
                    auto k = string_view(&s[i+j], L);
                    UMIT it = wcopy.find(k);
                    // UMIT it = wcopy.find(string_view(&s[i+j], L));
                    if(it == wcopy.end())
                        break;
                    else if(--it->second == 0)
                        wcopy.erase(it);
                }
                if(wcopy.empty())
                    rlt.emplace_back(i);
            }
        }
        return rlt;
    }
};

